  var k = 0;

  var result = [];

  while (i < (len_l) & j < (len_r)){
    //console.log(left[i], 'compared to', right[j]);

    if (left[i] <= right[j]) {
      result.push(left[i]);
      i += 1;
    } else {
      result.push(right[j]);
      j += 1;
    }
    k += 1;
    //console.log(len_l,i,len_r,j);
  }

  while (i < len_l){
    result.push(left[i]);
    i += 1;
  }
  while (j < len_r){
