function mergesort(i) {

  if (i.length < 2){
    return i
  }

  var len_i = Math.floor(i.length);

  var left = i.slice(0,len_i);
  var right = i.slice(len_i);

  return merge(mergesort(left),mergesort(right));
}



function merge(left, right) {

  var len_l = left.length - 1;
  var len_r = right.length - 1;

  var i = 0;
  var j = 0;
  var k = 0;

  var result = [];

  while (i <= (len_l) & j <= (len_r)){

    if (left[i] <= right[j]) {
      result.push(left[i]);
      i += 1;
    } else {
      result.push(right[j]);
      j += 1;
    }
    k += 1;
  }

  while (i <= len_l){
    result.push(left[i]);
    i += 1;
  }
  while (j <= len_r){
    result.push(right[j]);
    j += 1;
  }
  return result;
}

