import React from "react";
import uuid from "uuid";
import Bars from "./Bars";

import selectionsort from "./SelectionSort"
//import SortButton from "./SortButton"


const num_bars = 125;
const min_bar = 20;
const max_bar = 500;
const color1 = '#007bff';

export default class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      bar_list: []
    };
}

componentDidMount() {
  this.randomizebars();
}

  randomizebars = () => {
    this.setState({bar_list: GenerateBars(num_bars,min_bar,max_bar)});
  }

  updatebars = (updated_bars) => {

    var canProcede = false;
    for (var i = 0; i < updated_bars.length; i++) {

      setTimeout(() => {  canProcede = true; console.log("World!"); }, 2000);
      while (!canProcede){

      }
      canProcede = false;
    }

      this.setState({bar_list: updated_bars});

  }

  render() {
    const { bar_list } = this.state;

    return (
      <div>
        <div id="UI_container">
          <div id="button_container">
            <button className="btn btn-primary" onClick={() => this.randomizebars()}>Randomize</button>
            <button className="btn btn-primary" onClick={() => this.updatebars(selectionsort(this.state.bar_list))}>Selection Sort</button>
            <button className="btn btn-primary">Merge Sort</button>
            <button className="btn btn-primary">Quick Sort</button>
            <button className="btn btn-primary">Heap Sort</button>
            <button className="btn btn-primary">Bubble Sort</button>
          </div>
            <Bars bar_list={bar_list} />
        </div>
      </div>
    );
  }
}

function GenerateBars(len, min, max) {
  var lst = [];
  for (var i = 0; i < len; i++) {
    lst.push({id: uuid.v4(),len: getRandomInt(min, max),color: color1});
  }

  return lst;
}


function getRandomInt(min, max) {
  return Math.floor(Math.random() * (max - min) + min);
}
