import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

/*
class Bar extends React.Component{
  super(props)
  render() {
    <p>{this.props}</p>
  }
}
*/
function Bar(props) {
  const divStyle = {
    height: props.num,
  }
  return (
    <div id='bar' style={divStyle}></div>
  )
}


class Bars extends React.Component {
  render() {
    var max = 100

    var rows = [];
    for (var i = 0; i < max; i++) {
      rows.push(
        <Bar
        num={getRandomInt(200)}
        index={i}
        />
      );
    }
    return (
      rows
    );
  }
}

class SortButton extends React.Component {
  constructor(props) {
    super(props);
  }
   render() {
      return (<button onClick={props.randomizebars}>Sort</button>)
  }
}


class Visualizer extends React.Component {
  render() {
    return (
      <div id="UI_container">
        <div id="button_container">
          <SortButton onClick={() => this.randomizebars()} />
        </div>
        <div id="bar_container">
          <Bars />
        </div>
      </div>
    )
  }
}



ReactDOM.render(
    <Visualizer />,

  document.getElementById('root')
);


function getRandomInt(max) {
  return Math.floor(Math.random() * Math.floor(max));
}
