import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';



class Bar extends React.Component {
  constructor(props){
    super(props);
    this.divStyle = {
      height: props.num,
    }
  }
  render() {
    return(<div id='bar' style={this.divStyle}></div>)

  }
}


class Bars extends React.Component {
  constructor(props){
    super(props)
  }
  render() {
    var rows = [];
    for (var i = 0; i < props.values.length; i++) {
      rows.push(<div id='bar' style={{height: props.values.length[i]}}></div>)
    }
    /*
    var max = 100
    var rows = [];
    for (var i = 0; i < max; i++) {

      rows.push(
        <Bar
        num={getRandomInt(5,200)}
        index={i}
        />
      );
    }
    */

    return (
      rows
    );
  }
}

class SortButton extends React.Component {
   render() {
      return (<button onClick={() => this.props.randomizebars()}>Sort</button>)
  }
}


class Visualizer extends React.Component {

  render() {

    return (

      <div id="UI_container">
        <div id="button_container">
          <SortButton randomizebars={() => this.randomizebars()} />
        </div>
        <div id="bar_container">
          <Bars values={GenerateBars(10)}/>
        </div>
      </div>

    )
  }
}



ReactDOM.render(
    <Visualizer />,

  document.getElementById('root')
);

function GenerateBars(len,min,max) {
  var lst = [];
  for(i = 0; i < len; i++){
    lst[i] = Math.random() * (max - min) + min;
  }
  return(lst)

}
function getRandomInt(min, max) {
    return Math.random() * (max - min) + min;
}
