function mergesort(i, start, end, animations) {
  if (end - start === 1) {
    return i;
  }

  var length_of_list = Math.floor((end - start) / 2);

  console.log(i);
  //console.log("Length: ",length_of_list);
  console.log("Start: ", start, " End: ", end);
  console.log("Sliced:", i.slice(start, end));
  console.log(" ");

  i = mergesort(
    JSON.parse(JSON.stringify(i)),
    start,
    start + length_of_list,
    animations
  );
  i = mergesort(
    JSON.parse(JSON.stringify(i)),
    start + length_of_list,
    end,
    animations
  );

  i = merge(
    JSON.parse(JSON.stringify(i)),
    start,
    start + length_of_list,
    end,
    animations
  );

  return i;
}

