import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';



class Bar extends React.Component {
  constructor(props){
    super(props);
    this.divStyle = {
      height: props.num,
    }
  }
  render() {
    return(<div id='bar' style={this.divStyle}></div>)

  }
}


class Bars extends React.Component {
  render() {
    var rows = [];
    for (var i = 0; i < this.props.values.length; i++) {
      rows.push(<div id='bar' style={{height: this.props.values[i]}}></div>)
    }
    /*
    var max = 100
    var rows = [];
    for (var i = 0; i < max; i++) {

      rows.push(
        <Bar
        num={getRandomInt(5,200)}
        index={i}
        />
      );
    }
    */

    return (
      rows
    );
  }
}

class SortButton extends React.Component {
   render() {
      return (<button onClick={() => this.props.randomizebars()}>Sort</button>)
  }
}


class Visualizer extends React.Component {
  constructor() {
    super();
     this.state = {
       bar_values : GenerateBars(100),
     }
  }
  randomizebars() {
    this.setState({bar_values:GenerateBars(100)});
  }
  render() {

    return (

      <div id="UI_container">
        <div id="button_container">
          <SortButton randomizebars={() => this.randomizebars()} />
        </div>
        <div id="bar_container">
          <Bars values={this.state.bar_values}/>
        </div>
      </div>

    )
  }
}

function GenerateBars(len,min,max) {
  var lst = [];
  for(var i = 0; i < len; i++){
    lst.push(getRandomInt(0,200));
  }

  return(lst)

}

ReactDOM.render(
    <Visualizer />,
  document.getElementById('root')
);



function getRandomInt(min, max) {
    return Math.floor(Math.random() * (max - min) + min);
}
