  while (current_index < n-1) {
    var lowest = bar_list[current_index];
    var lowest_index;

      for (var i = current_index; i < n; i++) {
        if (bar_list[i]['len'] <= lowest['len']){
          lowest = bar_list[i];
          lowest_index = i;
        }
      }

      bar_list[lowest_index] = bar_list[current_index];
      bar_list[current_index] = lowest;
      current_index++;

  }
