function run_heapsort(a){

    var animations = [];
    create_heap(a, animations);
    //console.log("Heap Created:",a);

    for (var i = a.length - 1; i > 0; i--) {
      //console.log("Heap at",i,":",a);
      animations.push([0,i,"swap"])
      swap(a,i,0);
      heapify(a,i,0, animations);
    }
    console.log(animations);
    return animations;
}
export default run_heapsort;



function heapify(heap, n, index, animations){


  var largest = index;
  var left = 2 * index + 1;
  var right = 2 * index + 2;

  animations.push([left,largest,"compare"])
  if (left < n && heap[left] > heap[largest]){
    largest = left
  }
  animations.push([right,largest,"compare"])
  if (right < n && heap[right] > heap[largest]){
    largest = right
  }

  if (largest !== index){
    animations.push([index,largest,"swap"])
    swap(heap, largest, index);
    heapify(heap, n, largest, animations)
  }

}

function create_heap(heap, animations){

  var len_heap = heap.length;

  var start_index = Math.floor(len_heap / 2 - 1)

  for (var i = start_index; i > -1; i--) {
    heapify(heap, len_heap - 1, i, animations);
  }

}



function get_parent_index(heap,index){
    return(Math.ceil((index - 1) / 2));
}
function swap(input, index_A, index_B) {
   var temp = input[index_A];

   input[index_A] = input[index_B];
   input[index_B] = temp;
}




function test(){

  var test1 = [10,90,8,71,6,57,400,16,21,1]
  //console.log(test1);
  run_heapsort(test1);
  //console.log(test1);


}
